Add-Type -AssemblyName System.Windows.Forms;
Add-Type -AssemblyName System.Drawing;

function Show-UserPrompt {
    param([string]$Msg = "");

    $form = New-Object System.Windows.Forms.Form;
    $form.Text = "Create Administrator Account";
    $form.Size = New-Object System.Drawing.Size(360,300);
    $form.StartPosition = "CenterScreen";
    $form.Topmost = $true;

    $lblMsg = New-Object System.Windows.Forms.Label;
    $lblMsg.Text = $Msg;
    $lblMsg.Location = '20,10';
    $lblMsg.AutoSize = $true;
    $lblMsg.ForeColor = "Red";

    $lblUser = New-Object System.Windows.Forms.Label;
    $lblUser.Text = "Username:";
    $lblUser.Location = '20,40';
    $lblUser.AutoSize = $true;

    $txtUser = New-Object System.Windows.Forms.TextBox;
    $txtUser.Location = '20,60';
    $txtUser.Width = 300;

    $lblPass = New-Object System.Windows.Forms.Label;
    $lblPass.Text = "Password:";
    $lblPass.Location = '20,100';
    $lblPass.AutoSize = $true;

    $txtPass = New-Object System.Windows.Forms.TextBox;
    $txtPass.Location = '20,120';
    $txtPass.Width = 300;
    $txtPass.UseSystemPasswordChar = $true;

    $lblConfirm = New-Object System.Windows.Forms.Label;
    $lblConfirm.Text = "Confirm Password:";
    $lblConfirm.Location = '20,160';
    $lblConfirm.AutoSize = $true;

    $txtConfirm = New-Object System.Windows.Forms.TextBox;
    $txtConfirm.Location = '20,180';
    $txtConfirm.Width = 300;
    $txtConfirm.UseSystemPasswordChar = $true;

    $btnOK = New-Object System.Windows.Forms.Button;
    $btnOK.Text = "Create";
    $btnOK.Location = '130,220';
    $btnOK.DialogResult = [System.Windows.Forms.DialogResult]::OK;

    $form.AcceptButton = $btnOK;

    $form.Controls.AddRange(@($lblMsg,$lblUser,$txtUser,$lblPass,$txtPass,$lblConfirm,$txtConfirm,$btnOK));

    $Error = $False;
    $ErrorMsg = "";

    if ($form.ShowDialog() -ne 'OK') {
	$Error = $True;
        $ErrorMsg = "User cancelled.";
    }

    if ([string]::IsNullOrWhiteSpace($txtUser.Text)) {
	$Error = $True;
        $ErrorMsg = "Username cannot be empty.";
    }

    if ((Get-LocalUser).Name -contains $txtUser.Text) {
        $Error = $True;
        $ErrorMsg = "Username '$($txtUser.Text)' is already in use.";
    }

    if ($txtPass.Text -ne $txtConfirm.Text) {
	$Error = $True;
        $ErrorMsg = "Passwords do not match.";
    }

    return @{
        Username = $txtUser.Text;
        Password = $txtPass.Text;
	Error = $Error;
	ErrorMsg = $ErrorMsg;
    };
}

try {
    $promptMsg = "";
    do {
        $result = Show-UserPrompt $promptMsg;
        $promptMsg = $result.ErrorMsg;
        Write-Output "$promptMsg";
    } while ($result.Error);

    Write-Output "Creating user '$($result.Username)'";

    if ([string]::IsNullOrWhiteSpace($result.Password)) {
	Write-Output "Leaving password blank.";
        New-LocalUser -Name $result.Username -NoPassword -AccountNeverExpires -FullName $result.Username -ErrorAction Stop | Set-LocalUser -PasswordNeverExpires:$True -ErrorAction Stop;
    } else {
        New-LocalUser -Name $result.Username -Password $(ConvertTo-SecureString $result.Password -AsPlainText -Force) -PasswordNeverExpires -AccountNeverExpires -FullName $result.Username -ErrorAction Stop;
    }

    Add-LocalGroupMember -Group "Administrators" -Member $result.Username -ErrorAction Stop;

    Disable-LocalUser -Name "Administrator" -ErrorAction Stop;

    Write-Output "User created. Administrator disabled. Please Log out.";

    Start-Sleep 5;

} catch {
    Write-Error "Exception Occurred: $($_.Exception.Message)";
    exit 1;
}

